import React, { useContext } from 'react'
import { View, Text } from 'react-native';
import { SettingContext } from './Context'; 
import InforLine from '../../components/InforLine';
import Icon from 'react-native-vector-icons/Feather';
const LanguageChooseScreen = () => {
    const settings = useContext(SettingContext)
  return (
    <View>
        <TouchableWithoutFeedback onPress={() => settings.chooseLanguage('eng')}> 
            <InforLine title="Tiếng Anh" value={'eng'}   icon = {''}/>
        </TouchableWithoutFeedback>
        <TouchableWithoutFeedback onPress={() => settings.chooseLanguage('vie')}> 
            <InforLine title="Tiếng Việt" value={'vie'}  icon = {<Icon name={'check'} size={20} color={'#000'}/>}/>
        </TouchableWithoutFeedback>
    </View>
  );
};

export default LanguageChooseScreen;
