import { StyleSheet, Text, View,Dimensions } from 'react-native';
import { formatMoney } from '../extensions/formatMoney';
const Width  = Dimensions.get("window").width;
import React from 'react';

const CardMain = () => {
  return (
    <View style={[styles.card, styles.flexCenter]}>
      <Text style={[styles.white]}>Tổng tài sản</Text>
      <Text style={[styles.white, styles.title]}>{formatMoney(10000000)}</Text>
      <Text style={[styles.white]}>Lợi nhuận tháng này</Text>
    </View>
  );
};

export default CardMain;

const styles = StyleSheet.create({
    flexCenter: {
      alignItems: "center",  
      justifyContent: "flex-start", 
      flexDirection: "column",
      paddingVertical:10, 
    },
    card: {
        backgroundColor:'#020024',
        width:Width - 40,
        height:150,
        borderRadius:20,     
    },
    title: {
      fontSize:30,
    },
    white: {
      color:'#FFFFFF',
    },
    green: {
      color:'green',
    },
});
