const UserActions = (prevState, action) => {
    switch( action.type ) {
        case 'RETRIEVE_TOKEN': 
            return {
            ...prevState,
            userToken: action.token,
            isLoading: false,
            };
        case 'LOGIN': 
            return {
            ...prevState,
            userName: action.id,
            userToken: action.token,
            isLoading: false,
            };
        case 'LOGOUT': 
            return {
            ...prevState,
            userName: null,
            userToken: null,
            isLoading: false,
            };
        case 'REGISTER': 
            return {
            ...prevState,
            userName: action.id,
            userToken: action.token,
            isLoading: false,
            };
        default:
            return state
    }
  };

export {UserActions}
