import React from "react";
import { StyleSheet,ScrollView, View,Text, SafeAreaView } from "react-native";
import Analystic from '../assets/analystic.png'
import Chat from '../assets/chat.png'
import Transfer from '../assets/transfer.png'
import Icon from 'react-native-vector-icons/Feather';
import { Dimensions } from 'react-native';
import Header from '../components/Header'
import SliderCarousel from '../components/SliderCarousel'
import Option from '../components/Option'
import InforPrice from '../components/InforPrice'
const Height  = Dimensions.get("window").height; 
export default function App() {  
    let options = [ 
        {
            link:"/",
            title:"Thống kê",
            image:Analystic,
        },
        {
            link:"/",
            title:"Giao dịch",
            image:Transfer,
        },
        {
            link:"/",
            title:"Trao đổi",
            image:Chat,
        },

    ]
    let listoption = options.map((ele,index) => {
        return <Option image={ele.image} title={ele.title} link={ele.link} key={index}/> 
    })

  return (
    <>
    <Header />    
        <SafeAreaView  style={styles.boxContainer}>
        <ScrollView style={styles.boxContainer}>
            <View>
                <SliderCarousel />
            </View>
            <View style={styles.options}>
                {listoption}
            </View>
            <View style={styles.information}>  
                <InforPrice />
            </View> 
        </ScrollView> 
        </SafeAreaView>  
    {/* <View style={styles.container}>
        <View style={styles.options}>
            {listoption}
        </View>
    </View> */}
    </>
  );
}

const styles = StyleSheet.create({
    container: { 
        paddingVertical:50, 
    }, 
    options: { 
        alignItems: "center",  
        justifyContent: "space-around", 
        flexDirection: "row",
        borderRadius:25,
        paddingVertical:18, 
    },
    boxContainer:{ 
        backgroundColor: "#fff4eb",
        paddingVertical:10, 
        height:Height,
        borderRadius:25, 
    },
    
});
