import { StyleSheet, Text, View } from 'react-native';
import { formatMoney } from '../extensions/formatMoney';  
import styleGlobal from '../assets/css/globalStyle'
import React from 'react';

const InforLine = (props) => {
  return (
    <View style={[styleGlobal.flexBetween ,styles.container]}> 
        <Text style={styles.title}>{props.title}</Text> 
        {
          props
        }
        <Text  style={[styles.title,styleGlobal.bold]}>{props.value ? formatMoney(props.value) : "-"} <Text style={[styles.param]}>VNƒê</Text></Text>    
    </View>
  );
};

export default InforLine;

const styles = StyleSheet.create({ 
  container: {
    height:50, 
    paddingHorizontal:20, 
    borderBottomWidth: 1,
    borderBottomColor: 'lightgrey',
  },
  title: {  
    fontSize:15,
    color:'#000'
  },
  param: {
    fontSize:13,
    color:'#c2c0c0'
  }
});
