import { useState, useEffect,useMemo,useContext } from 'react'
import { 
  NavigationContainer,  
} from '@react-navigation/native';
import { View,StyleSheet,ActivityIndicator } from 'react-native';
import { useUserActions } from '../hook/useUserActionsContext';  
import { UserContext } from '../screens/UserConfirm/Context'
import RootScreen from '../screens/UserConfirm/RootScreen';
import HomeScreen from '../screens/HomeScreen'
import Tabar from "../components/Tabar";
import React from 'react';

const HandleScreen = () => {
    const { userToken,isLoading } = useContext(UserContext)
  const { checkUserLogin } = useUserActions()
  
  useEffect(() => {
    setTimeout(async() => {
        await  checkUserLogin()
    }, 1000); 
  }, []); 
    if( isLoading ) {
        return(
          <View style={{flex:1,justifyContent:'center',alignItems:'center'}}>
            <ActivityIndicator size="large"  color="#1b242d"/>
          </View>
        );
      }
  return (
    <>
      { userToken !== null ? (
          <Tabar />
      )
      :
        <NavigationContainer >
        <RootScreen/> 
      </NavigationContainer>
    }
      
    </>
  );
};

export default HandleScreen;

const styles = StyleSheet.create({});
