import { StyleSheet, Text, View } from 'react-native';
import { formatMoney } from '../extensions/formatMoney';  
import styleGlobal from '../assets/css/globalStyle'
import Icon from 'react-native-vector-icons/Feather';
import React from 'react';

const InforLine = (props) => {
  return (
    <View style={[styleGlobal.flexBetween ,styles.container]}> 
        <Text style={styles.title}>{props.title}</Text> 
        {
          props.stack === false && <Text  style={[styles.title,styleGlobal.bold]}>{props.value ? formatMoney(props.value) : "-"} <Text style={[styles.param]}>VNĐ</Text></Text>
        }
        {
          props.stack === true && <View style={[styleGlobal.flexCenter]}><Text>Tiếng Việt</Text><View><Icon name={'chevron-right'} size={20} color={'#939393'} /></View></View>
        }           
    </View>
  );
};

export default InforLine;

const styles = StyleSheet.create({ 
  container: {
    height:50, 
    paddingHorizontal:20, 
    borderBottomWidth: 1,
    borderBottomColor: 'lightgrey',
  },
  title: {  
    fontSize:16,
    color:'#000'
  },
  param: {
    fontSize:13,
    color:'#c2c0c0'
  }
});
