import { formatMoney } from '../extensions/formatMoney'; 
import styleGlobal from '../assets/css/globalStyle'
import React from 'react';

const InforLine = () => {
  return (
    <View styles={[styleGlobal.flexBetween,styleGlobal.width100]}>
      <Text>TS r√≤ng TKCK</Text>
      <Text>{formatMoney(1200000)}</Text>
    </View>
  );
};

export default InforLine;

const styles = StyleSheet.create({

  flexBetween: {
    alignItems: "center",  
    justifyContent: "space-between", 
    flexDirection: "row",
    flex:1,
  },
});
