import { AsyncStorage } from '@react-native-community/async-storage';
const Storage = async (userInfor) => {   
    try {
        await AsyncStorage.setItem('userToken', userInfor);
    } catch(e) {
        console.log(e);
    }
} 
const checkStorageUser = async (target) => {   
    let userToken = null; 
    try {
        userToken = await AsyncStorage.getItem(target);
    } catch(e) {
        console.log(e);
    }
    return userToken;

} 
const data = ['lukim','kimei']
const UserActions = (prevState, action) => {
    switch( action.type ) {
        case 'RETRIEVE_TOKEN': 
            return {
            ...prevState,
            userToken: prevState.token,
            isLoading: false,
        };
        case 'LOGIN':   
            return {
            ...prevState,
            userName:prevState.userName,
            userToken: prevState.userToken,
            isLoading: false,
        };
        case 'LOGOUT': 
            return {
            ...prevState,
            userName: null,
            userToken: null,
            isLoading: false,
        };
        case 'REGISTER': 
            return {
            ...prevState,
            userName: prevState.id,
            userToken: prevState.token,
            isLoading: false,
        };
        case 'CHECK_USER_LOGIN':  
            
            if(data.includes(JSON.stringify(checkStorageUser('userToken')), 0)) {
                
                alert("OK")
                return {
                    ...prevState, 
                    isLoading: false,
                };
            }
            else {
                return {
                    ...prevState,
                    userName: null,
                    userToken: null,
                    isLoading: false,
                };
            }
        default:
            return prevState
    }
  };

export {UserActions}
