import * as React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs'; 
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import Icon from 'react-native-vector-icons/Feather';

// Screens
import HomeScreen from '../screens/HomeScreen';
import AccountScreen from '../screens/AccountScreen/AccountScreen';
import SettingsScreen from '../screens/SettingScreen/SettingsScreen';

//Screen names
const homeName = "Home";
const accountName = "Tài khoản";
const settingsName = "Cài đặt";

const Tab = createBottomTabNavigator();
const SettingsStack = createNativeStackNavigator();
const HomeStack = createNativeStackNavigator();
function SettingssssssScreen({ navigation }) {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Text>Settings Screen</Text>
      <Button
        title="Go to Profile"
        onPress={() => navigation.navigate('Profile')}
      />
    </View>
  );
}

function ProfileScreen({ navigation }) {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Text>Profile Screen</Text>
      <Button
        title="Go to Settings"
        onPress={() => navigation.navigate('Settings')}
      />
    </View>
  );
}
function DetailsScreen({ navigation }) {
  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Text>Details Screen</Text>
      <Button
        title="Go to Details... again"
        onPress={() => navigation.push('Details')}
      />
    </View>
  );
}
function MainContainer() {
  return (
    <NavigationContainer>
      <Tab.Navigator 
      screenOptions={({ route }) => ({
        tabBarActiveTintColor: '#020024',
        tabBarInactiveTintColor: 'grey',
        tabBarStyle: { paddingBottom: 0, fontSize: 10 }, 
        tabBarShowLabel: false ,  
        headerTitleAlign: 'center',
        tabBarIcon: ({ focused, color, size }) => {
          let iconName;
          let rn = route.name;

          if (rn === homeName) {
            iconName = focused ? 'home' : 'home';

          } else if (rn === accountName) {
            iconName = focused ? 'user' : 'user';

          } else if (rn === settingsName) {
            iconName = focused ? 'settings' : 'settings';
          } 
          return <Icon name={iconName} size={23} color={color} />;
        },
      })}>
        <Tab.Screen  name={homeName} component={HomeScreen}  options={{headerShown: false}}>
          {() => (
            <SettingsStack.Navigator>
              <SettingsStack.Screen
                name="Settings"
                component={SettingsScreen}
              />
              <SettingsStack.Screen name="Profile" component={ProfileScreen} />
            </SettingsStack.Navigator>
          )}
        </Tab.Screen>
        <Tab.Screen  name={accountName} component={AccountScreen}>
          {() => (
            <HomeStack.Navigator>
              <HomeStack.Screen name="Home" component={HomeScreen} />
              <HomeStack.Screen name="Details" component={DetailsScreen} />
            </HomeStack.Navigator>
          )}
        </Tab.Screen> 
        <Tab.Screen name={settingsName} component={SettingsScreen} />
      </Tab.Navigator> 
      
    </NavigationContainer>
  );
}

export default MainContainer;
