const Storage = (userInfor) => { 
    localStorage.setItem('user', userInfor);  
}
const data = ['lukim','kimei']
const UserActions = (prevState, action) => {
    switch( action.type ) {
        case 'RETRIEVE_TOKEN': 
            return {
            ...prevState,
            userToken: action.token,
            isLoading: false,
        };
        case 'LOGIN': 
            return {
            ...prevState,
            userName: action.id,
            userToken: action.token,
            isLoading: false,
        };
        case 'LOGOUT': 
            return {
            ...prevState,
            userName: null,
            userToken: null,
            isLoading: false,
        };
        case 'REGISTER': 
            return {
            ...prevState,
            userName: action.id,
            userToken: action.token,
            isLoading: false,
        };
        case 'CHECK_USER_LOGIN': 
            if(data.includes(JSON.stringify(localStorage.getItem('userToken')), 0)) {
                
                alert("OK LOGIN")
                
            }
            else {
                return {
                    ...prevState,
                    userName: null,
                    userToken: null,
                    isLoading: false,
                };
            }
        default:
            return prevState
    }
  };

export {UserActions}
