import React, { useContext } from 'react'
import { View, Text,TouchableHighlight } from 'react-native';
import { SettingContext } from './Context'; 
import InforLine from '../../components/InforLine';
import Icon from 'react-native-vector-icons/Feather';
const ModeThemeScreen = () => {
    const settings = useContext(SettingContext) 
  return (
    <> 
        <TouchableHighlight onPress={() => settings.chooseTheme('eng')} underlayColor="white"> 
            <InforLine title="Sáng" value={'light'}   icon = {settings.options.theme==='light' && <Icon name={'check'} size={20} color={'#000'}/>}/>
        </TouchableHighlight>
        <TouchableHighlight onPress={() => settings.chooseLanguage('vie')} underlayColor="white"> 
            <InforLine title="Tối" value={'dark'}  icon = {settings.options.theme==='dark' && <Icon name={'check'} size={20} color={'#000'}/>}/>
        </TouchableHighlight>
    </>
  );
};

export default ModeThemeScreen;
