import React,{ useState, useEffect,useMemo,useContext } from 'react'
import { 
  NavigationContainer,  
} from '@react-navigation/native';
import { View,StyleSheet,ActivityIndicator } from 'react-native';
import { useUserActions } from '../hook/useUserActionsContext';  
import { UserContext } from '../screens/UserConfirm/Context';
import { checkStorageUser } from '../actions/UserActions'
import RootScreen from '../screens/UserConfirm/RootScreen';
import AsyncStorage from '@react-native-async-storage/async-storage'
import Tabar from "../components/Tabar"; 

const HandleScreen = () => {
  const { userName, isLoading,isUser } = useContext(UserContext) 
  const { redirectUser,retrieveToken } = useUserActions()
  
  useEffect(() => {
    setTimeout( async () => {  
      checkStorageUser('userToken').then(data => {
        if(dataUser.includes(data,0)) { 
            return {
                ...prevState,
                isLoading: false,
                isUser:true,
            };  
        }
        else {
            return {
                ...prevState,
                userName: null,
                passWord: null, 
                isLoading: false,
                isUser:false,
            };
        } 
    })
    }, 1000); 
  }, [userName]); 
  if( isLoading ) {
        return (
          <View style={{flex:1,justifyContent:'center',alignItems:'center'}}>
            <ActivityIndicator size="large"  color="#1b242d"/>
          </View>
        );
  }
  return (
    <>
      { 
        isUser ? (
          <Tabar />
        )
        :
        <NavigationContainer >
          <RootScreen/> 
        </NavigationContainer>
      }
      
    </>
  );
};

export default HandleScreen;

const styles = StyleSheet.create({});
